import React, { Component } from 'react';
import { motion } from 'framer-motion';
import Link from 'next/link'

const PageVariants = {
  initial: {
    opacity: 0,
    x: "-100vw",
    scale: 0.8
  },
  in: {
      opacity: 1,
      x: 0,
      scale: 1
  },
  out: {
      opacity: 0,
      x: "-100vw",
      scale: 0.8
  }
};

const PageTransition = {
  type: "tween",
  ease: "anticipate",
  duration: 1
};

export default class Index extends React.Component {

  state = {
    loading: true
  };

  async componentDidMount() {
    const url = 'http://vkovalov.me/wp-json/wp/v2/pages/17';
    const response = await fetch(url);
    const data = await response.json();
    this.setState({ page: data, loading: false });
    const page = this.state.page;
  }

  render() {
    return (
      <motion.div 
          initial="out"
          animate="in"
          exit="out"
          variants={PageVariants}
          transition={PageTransition}
          id="home-content">
          {this.state.loading || !page ? (
            <div>Loading...</div>
          ) : (
            <div class="content-inside">
              <h1 className="uppercase text-center">
                <span className="firstname">{page.acf.firstname} </span>  
                <span className="lastname">{this.state.page.acf.lastname}</span>
              </h1>
              <button className="btn">
                <Link href={this.state.page.acf.cb_link}>
                  {this.state.page.acf.cb_text}
                </Link>
              </button>
            </div>
          )} 
      </motion.div> 
    )
  }
}
