import React, { Component } from 'react';
import { motion } from 'framer-motion';

const PageVariants = {
  initial: {
    opacity: 0,
    x: "-100vw",
    scale: 0.8
  },
  in: {
      opacity: 1,
      x: 0,
      scale: 1
  },
  out: {
      opacity: 0,
      x: "-100vw",
      scale: 0.8
  }
};

const PageTransition = {
  type: "tween",
  ease: "anticipate",
  duration: 1
};

export default class Index extends React.Component {

  state = {
    loading: true
  };

  async componentDidMount() {
    const url = 'http://vkovalov.me/wp-json/wp/v2/pages/17';
    const response = await fetch(url);
    const data = await response.json();
    this.setState({ page: data.results[0], loading: false });
    console.log(data);
  }

  render() {
    return (
      <motion.div 
          initial="out"
          animate="in"
          exit="out"
          variants={PageVariants}
          transition={PageTransition}
          id="home-content">
          <h1 className="uppercase">
            <span className="firstname">Volodymyr</span> 
            <span className="lastname">Kovalov</span>
          </h1>
      </motion.div> 
    )
  }
}
