import React, { Component } from 'react'
import axios from 'axios'
import * as ReactBS from 'react-bootstrap'
import InfiniteCarousel from 'react-leaf-carousel';


export default class ProjectsSlider extends React.Component {

  constructor (props) {
    super(props);
    this.state = {
      loading: false,
      projects: [],
      error: ''
    }
  }

  componentDidMount() {
    const rest = process.env.NEXT_PUBLIC_REST_LINK;
    const url = rest+'wp-json/wp/v2/projects?filter[orderby]=date&order=asc';
    this.setState(
      { loading: true }, () =>{
        axios.get(url)
          .then(res => { 
              this.setState({
                loading: false,
                projects: res.data
              })
            }
          )
          .catch( 
            error => this.setState({ 
            loading: false, 
            error: '' 
          }))  
      }
    );
  }
  render() {
    const { projects } = this.state;
    return (
      <ReactBS.Row>
        <ReactBS.Col>
            {projects.length ? (
                <InfiniteCarousel
                    breakpoints={[
                    {
                        breakpoint: 500,
                        settings: {
                        slidesToShow: 1,
                        slidesToScroll: 1,
                        },
                    },
                    {
                        breakpoint: 768,
                        settings: {
                        slidesToShow: 2,
                        slidesToScroll: 1,
                        },
                    },
                    ]}
                    dots={false}
                    showSides={true}
                    sidesOpacity={0}
                    sideSize={.1}
                    slidesToScroll={1}
                    slidesToShow={3}
                    scrollOnDevice={true}
                >
                    {projects.map(project => (
                        <div className="project-slide" id={project.id}>
                            <div>
                                <img src={project.acf.cover} alt="project.title.rendered" />
                            </div>
                            <span>{project.title.rendered}</span>
                            <span>{project.title.acf.type}</span>
                        </div>
                    ))} 
                </InfiniteCarousel>    
            ) : '' }
        </ReactBS.Col>
      </ReactBS.Row>
    )
  }
}
