class Menu extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        error: null,
        isLoaded: false,
        items: []
      };
    }

    componentDidMount() {
      fetch("http://vkovalov.me/back/wp-json/menus/v1/locations/primary")
        .then(res => res.json())
        .then(
          (result) => {
            this.setState({
              isLoaded: true,
              items: result.items
            });
          },
          (error) => {
            this.setState({
              isLoaded: true,
              error
            });
          }
        )
    }

    render() {
      const { error, isLoaded, items } = this.state;
      if (error) {
        return <div>Помилка: {error.message}</div>;
      } else if (!isLoaded) {
        return <div>Завантаження...</div>;
      } else {
        return (
          <ul>
            {items.map(item => (
              <li>
                {item.title} 
              </li>
            ))}
          </ul>
        );
      }
    }
  }

export default Menu
