import React, { Component } from 'react'
import axios from 'axios'
import * as ReactBS from 'react-bootstrap'
import AliceCarousel from 'react-alice-carousel'


export default class ProjectsSlider extends React.Component {

  constructor (props) {
    super(props);
    this.state = {
      loading: false,
      projects: [],
      error: ''
    }
  }

  componentDidMount() {
    const rest = process.env.NEXT_PUBLIC_REST_LINK;
    const url = rest+'wp-json/wp/v2/projects?filter[orderby]=date&order=asc';
    this.setState(
      { loading: true }, () =>{
        axios.get(url)
          .then(res => { 
              this.setState({
                loading: false,
                projects: res.data
              })
            }
          )
          .catch( 
            error => this.setState({ 
            loading: false, 
            error: '' 
          }))  
      }
    );
  }
  render() {
    const { projects } = this.state;
    return (
      <ReactBS.Row>
        {projects.length ? (
          <ReactBS.Col>
              <AliceCarousel>
                {projects.map(project => (
                    <div className="project-slide" id={project.id}>
                        {project.title.rendered}
                    </div>
                ))}
            </AliceCarousel>
          </ReactBS.Col>
        ) : '' }
      </ReactBS.Row>
    )
  }
}
