import React, { Component } from 'react'

export default class Positions extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        error: null,
        isLoaded: false,
        items: []
      };
    }

    componentDidMount() {
      fetch("http://vkovalov.me/wp-json/wp/v2/pages/17")
        .then(res => res.json())
        .then(
          (result) => {
            this.setState({
              isLoaded: true,
              positions: result.acf.positions
            });
          },
          // Примітка: важливо обробляти помилки саме тут,
          // а не в блоці catch (), щоб не перехоплювати
          // виключення з помилок в самих компонентах.
          (error) => {
            this.setState({
              isLoaded: true,
              error
            });
          }
        )
    }

    render() {
      const { error, isLoaded, items } = this.state;
      if (error) {
        return <div>Помилка: {error.message}</div>;
      } else if (!isLoaded) {
        return <div>Завантаження...</div>;
      } else {
        return (
          <ul>
            {acf.positions.map(position => (
              <li key={position}>
                {position}
              </li>
            ))}
          </ul>
        );
      }
    }
  }
