import React, { Component } from 'react'

export default class Positions extends React.Component {
  state = {
    loading: true,
    positions: []
  };

  async componentDidMount() {
    const url = 'http://vkovalov.me/wp-json/wp/v2/pages/19';
    const response = await fetch(url);
    const data = await response.json();
    this.setState({ 
      page: data,
      positions: page.acf.positions, 
      loading: false
    });
  }

  render() {
    return (
      <ul className="positions d-flex justify-content-center">
        <li className="position mx-4">
          Freelancer
        </li>
      </ul>
    );
  }
}
