import * as ReactBS from 'react-bootstrap'
import Link from 'next/link'

class MyComponent extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        error: null,
        isLoaded: false,
        items: []
      };
    }

    componentDidMount() {
      fetch("https://api.example.com/items")
        .then(res => res.json())
        .then(
          (result) => {
            this.setState({
              isLoaded: true,
              items: result.items
            });
          },
          // Примітка: важливо обробляти помилки саме тут,
          // а не в блоці catch (), щоб не перехоплювати
          // виключення з помилок в самих компонентах.
          (error) => {
            this.setState({
              isLoaded: true,
              error
            });
          }
        )
    }

    render() {
      const { error, isLoaded, items } = this.state;
      if (error) {
        return <div>Помилка: {error.message}</div>;
      } else if (!isLoaded) {
        return <div>Завантаження...</div>;
      } else {
        return (
          <ul>
            {items.map(item => (
              <li>
                {item.title} 
              </li>
            ))}
          </ul>
        );
      }
    }
  }

function Header() {
  return (
    <ReactBS.Navbar bg="dark" variant="dark" expand="lg">
        <ReactBS.Navbar.Toggle aria-controls="basic-navbar-nav" />
        <ReactBS.Navbar.Collapse id="basic-navbar-nav">
            <ReactBS.Nav className="mx-auto">
                <Link class="nav-link" href="/">Home</Link>
                <Link class="nav-link" href="/about">About</Link>
            </ReactBS.Nav>
        </ReactBS.Navbar.Collapse>
    </ReactBS.Navbar>
  )
}

export default Header
